import React, { useEffect, useState } from "react";


function Home() {

    const [greeting, setGreeting] = useState<string>("Hello2");

    useEffect(() => {
        const intervalId: NodeJS.Timeout = setInterval(() => {
            setGreeting(updateGreetingLanguage());
            // TODO: Change the inter
        }, 10000);
        return () => clearInterval(intervalId);
    })

    return (
        <>
            <p>{greeting}</p>

        </>
    );
}

export default Home;
function updateGreetingLanguage(): string {
    //TODO: Provide more language
    let greetingLanguage: string[] = ["Hello", "你好"];
    return greetingLanguage[Math.floor(Math.random() * greetingLanguage.length)];
}
