import React, { useEffect, useState } from "react";
import './Home.css';

function Home() {

    const [greeting, setGreeting] = useState<string>("Hello"),
    [greetingBgColor, setGreetingBgColor] = useState<st>



    useEffect(() => {
        const intervalId: NodeJS.Timeout = setInterval(() => {
            setGreeting(updateGreetingLanguage());
            let randomColor = Math.floor(Math.random() * 16777215).toString(16);
            hello.style.color = "#" + randomColor;

            // TODO: Change the interval time
        }, 10000);
        return () => clearInterval(intervalId);
    })

    return (
        <>
            <section id="firstPageBox">
                <div id="leftBox">
                    <div className="profile_pic"></div>
                    <button className="resume" >Resume</button>
                </div>


                <div id="rightBox">
                    {/* <!-- For the title hello world --> */}
                    <h1 id="hello_world"><span id="hello" style={{ backgroundColor: 'your-color-value' }}>{greeting}</span> World!</h1>
                    {/* <!-- The introduction --> */}
                    <p id="self-introduction">
                        I’m Howard Tung. A software developer from Taiwan. I like to design
                        and making creative, innovative, and unique projects. Feel free to
                        contact me if you have any questions.
                    </p>
                    {/* <!-- Icon to social media (LinkedIn, Github, Mail) -->
                <!--      Distributed evenly      --> */}
                    <div id="socialAPP">
                        <a href="https://www.linkedin.com/in/justhoward0807/" rel="noreferrer" target="_blank">
                            <img
                                className="social"
                                src="images/linkedin-icon-2.svg"
                                alt="LinkedIn"
                                height="35px"
                                width="35px"

                            /></a>
                        {/* <!-- Jump to email me section --> */}
                        <a>
                            <img
                                className="social"
                                src="images/gmail.ico"
                                alt="Email"
                                width="35px"
                                height="35px"
                            />
                        </a>
                        <a href="https://github.com/JustHoward0807" rel="noreferrer" target="_blank">
                            <img
                                className="social"
                                src="images/github-mark-white.svg"
                                alt="Github"
                                width="35px"
                                height="35px"
                            />
                        </a>
                    </div>
                </div>
            </section>

        </>
    );
}

export default Home;

function updateGreetingLanguage(): string {
    //TODO: Provide more language
    let greetingLanguage: string[] = ["Hello", "你好"];
    return greetingLanguage[Math.floor(Math.random() * greetingLanguage.length)];
}
